package strings;

public class DisplayOnlyAlphabetsFromGivenArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char[] arr= {'a','b','v','@','1','2','3'};
		
		int count=0;

		for(int i=0;i<=arr.length-1;i++)
		{
				count++;
		}
		System.out.println(count);


	}

}
